local Player = exports.wx_module_system:RequestModule("Player")
local Utils = exports.wx_module_system:RequestModule("Utils")
TriggerEvent("RegisterItem",{
    ItemName = "water",
    ItemShowName = "水",
    ItemType = "food",
    ItemDescription = "水[+30-40%干渴度]",
    ItemDensity = 0.8,
    ItemModel = "bkr_prop_coke_bottle_01a",
    ItemMaxDensity = 5000000,
    ItemMaxAmount = 500,
    ItemMaxUseAmount = 1,
    ItemMaxTransferAmount = 1000000000,
    ItemMaxThrowAmount = 1000000000,
    ItemMaxStack = 1000000000,
    --#region image
    ItemImage = " data:image/jpeg;base64,",
    --#endregion
    CanItemDrop = true,
    CanItemPickup = true,
    CanItemUse = true,
    CanItemTransfer = true,
    CanItemCombine = true,
    IsItemPhysicalAfterDrop = true,
    UseFunction = function(PlayerID,ItemAmount,AttachData,Reject)
        print(PlayerID)
        local a = Player.GetPlayer(PlayerID)
        a = a:Physiology()
        local addValue = Utils.GenerateRandomInt(30,40)
        if a.Physiology.Thirst.Get() + addValue < 100 then
            a.Physiology.Thirst.Add(addValue)
        else
            a = a:Notification()
            a.Notification.ShowNotification("喝不下了")
            Reject()
        end
    end,
    DropFunction = nil,
    PickupFunction = nil,
    StackFunction = nil,
    TransferFunction = nil,
    IsEnableAntiCheat = true,
})