local Callback = exports.wx_module_system:RequestModule("Callback")

RegisterNUICallback("quit",function(data,cb)
    SetNuiFocus(false,false)
end)


RegisterNUICallback("itemAction",function(data,cb)
    if data.action == "使用" then
        TriggerServerEvent("wx_inventory_ui:useItem",data.itemName,data.amount)
    elseif data.action == "丢弃" then
        TriggerServerEvent("wx_inventory_ui:throwItem",data.itemName,data.amount)
    elseif data.action == "转移" then
        --TODO: 选择对象
    end
    SetNuiFocus(false,false)
    SendNUIMessage({
        type = "close",
    })
end)



RegisterCommand('+openInventory', function()
    local inventory = Callback.TriggerServerCallback("wx_inventory_ui:getInventory")
    SetNuiFocus(true,true)
    SendNUIMessage({
        type = "open",
        inventory = inventory 
    })
end)
RegisterCommand('-openInventory', function()end)



RegisterKeyMapping('+openInventory', '打开背包', 'keyboard', 'b')

